import React, {useState} from 'react';
import { useSpring, animated } from 'react-spring'

const Groupe5 = () => {

    const [animation, setAnimation]=useState(false)

    const props = useSpring({ to: { opacity: 1 }, 
                            from: { opacity: 0 },  
                            config: { duration: 10000 } })
                            const MysteriousText = ({ children, ...props }) => {
                                const animation = i =>
                                  useSpring({ opacity: 1, from: { opacity: 0 }, delay: Math.random() * 350 });
                                return children.split("").map((item, index) => (
                                  <animated.span key={index} style={animation(index)} {...props}>
                                    {item}
                                  </animated.span>
                                ));
                              };

    return (
        <div>
     <button onClick={()=>setAnimation(!animation)}>click</button>

     {animation ? <animated.div style={props}>
        I will fade in
        </animated.div> : null}
     </div>
    )
  }

export default Groupe5;
