import React from 'react';
import { useTrail, animated } from 'react-spring';

const Groupe5 = () => {
    onst [items, setItems] = useState(NUM_TRANS)

    const transitions = useTransition(items, {
      from: { opacity: 0 },
      enter: { opacity: 1 },
      leave: { opacity: 0 },
      delay: 200,
      config: config.molasses,
      onRest: () => setItems([]),
    })

    useEffect(() => {
      if (items.length === 0) {
        setTimeout(() => {
          setItems(NUM_TRANS)
        }, 2000)
      }
    }, [items])

    return (
      <div style={{ display: 'flex' }}>
        {transitions(({ opacity }, item) => (
          <animated.div
            style={{
              opacity: opacity.to(item.op),
              transform: opacity
                .to(item.trans)
                .to(y => `translate3d(0,${y}px,0)`),
            }}>
            {item.fig}
          </animated.div>
        ))}
      </div>
    )
  }
